package controlstatement;

public class ElseIfStatement1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String season="winter";
		
		if(season=="winter")
		{
			System.out.println("temprature in winter between 15 to 25 dc");

		}
		
		else if(season=="rainy")
		{
			System.out.println("temprature in raniy between 20 to 30 dc");

		}
		
		else if (season=="summer")
		{
			System.out.println("temprature in summer between 25 to 50 dc");

		}
		
	}

}
