package loops;

public class ForLoopstudy {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("increment");
		for(int i=1; i<=10; i++) //increment
		{
			System.out.println(i);
		}
		
		System.out.println("decrement");
		for(int i=10; i>=1; i--) //decrement
		{
			System.out.println(i);
		}
		
		System.out.println("9th table for increment");
		for(int n=9; n<=90; n=n+9) //9th table for increment
		{
			System.out.println(n);
		}
		
		System.out.println("5th table for decrement");
		for(int m=50; m>=5; m=m-5) // 5th table for decrement
		{
			System.out.println(m);
		}
		
	}

}
